üîóABC-368 A - Cut



 ÊèêÂá∫ÁµêÊûú
def getIntMap():
    return map(int, input().split())


def getStringList():
    return list(input().split())


def main():
    N, K = getIntMap()
    A = getStringList()

    print(" ".join((A[(-1 * K) :] + A[: (N - K)])))


if __name__ == "__main__":
    main()

