def getInt():
    return int(input())


def getIntList():
    return list(map(int, input().split()))


def main():
    N = getInt()
    A = getIntList()

    B = [A[i] - A[i - 1] for i in range(1, N)]

    print("Yes" if min(B) &gt; 0 else "No")...

