def foldr(f, z, xxs):
    if xxs == []:
        return z

    x = xxs[0]
    xs = xxs[1:]
    return f(x, foldr(f, z, xs))


def concat(xxs):
    plus = lambda x, y: x + y
    return foldr(plus, [], xxs)...
