def getIntMap():
    return map(int, input().split())


def main():
    R, X = getIntMap()

    print("Yes" if (X == 1 and 1600 &lt;= R &lt;= 2999) or (X == 2 and 1200 &lt;= R &lt;= 2399) else "No")


if __name__ == "__main__":
    main()

