const JST_OFFSET_MS = 9 * 60 * 60 * 1000;

export const getJSTFullYear = (date: Date): number =&gt; {
  const jstDate = new Date(date.getTime() + JST_OFFSET_MS);
  return jstDate.getUTCFullYear();
};

